      <div class="form-item-label">邀请码({{ requireInviteCode ? '必填' : '选填' }})</div>
      <div class="input-box">
<!--        <input v-model.trim="loginName" placeholder="请输入邀请码" autocomplete="off" />-->
        <input
          type="text"
          v-model.trim="formData.activeCode"
          maxlength="140"
          placeholder="请输入邀请码"
          class="uni-input-input"
          autocomplete="off"
        />
      </div>
    </div>

