      <div class="form-item-label">验证码</div>
      <div class="input-box">
<!--        <input placeholder="请输入验证码" v-model.trim="code" autocomplete="off" />-->
        <input
          v-model.trim="formData.code"
          maxlength="140"
          placeholder="请输入验证码"
          class="uni-input-input"
          autocomplete="off"
        />
        <div class="icon">
          <img @click="refreshCode" style="width: 80px; height: 30px" :src="codeUrl" />
        </div>
      </div>
    </div>

    <div class="btn" @click="handleSubmit">
      <div v-if="loading" @click="handleSubmit">加载中...</div>
      <div v-else @click="handleSubmit">
        注册
      </div>
    </div>

    <div class="text">
      <span @click="router.push('/login')">立即登入</span>
    </div>

  </div>
</template>

<script setup>
import { useRouter } from 'vue-router'
import { showToast } from 'vant'
import { signUp } from '@/api/user'
import { useUserStore } from '@/store/user/index'
import { useMainStore } from '@/store/index'
const mainStore = useMainStore()
const userStore = useUserStore()
const router = useRouter()
const isPwd = ref(true)
const isPwd2 = ref(true)
const timestamp = ref(+new Date())
const codeUrl = computed(() => {
  return `${mainStore.verificationCodeUrl}?codeType=LOGIN&timestamp=${timestamp.value}`
})
// 刷新验证码
const refreshCode = () => {
  timestamp.value = +new Date()
}
const pwd = ref('')
const formData = ref({
  loginName: '', //用户名/邮箱/手机号
  loginPassword: '', //密码
  activeCode: '', //邀请码
  code: '', //验证码
  signType: 3
})
const requireInviteCode = computed(() => {
  return mainStore.getLoginMethodList.inviteCodeRequired ? true : false
})
const loading = ref(false)
const autoLogin = computed(() => {
  return mainStore.getLoginMethodList.autoLogin
})
const showVerifyCode = computed(() => {
  return mainStore.getLoginMethodList.accountCode
})
const handleSubmit = () => {
  if (!formData.value.loginName.trim()) {
    showToast('请输入账号')
    return
  }
  // 用户名小写字母+数字限制6-15位
  if (!/^[a-z0-9]{6,15}$/.test(formData.value.loginName.trim())) {
    showToast('请填写6-15位包含数字字母的账号')
    return
  }
  if (!formData.value.loginPassword.trim()) {
    showToast('请输入登陆密码')
    return
  }
  if (!pwd.value.trim()) {
    showToast('请再次输入登陆密码')
    return
  }
  if (showVerifyCode.value && !formData.value.code.trim()) {
    showToast('请输入验证码')
    return
  }
  if (requireInviteCode.value && !formData.value.activeCode.trim()) {
    showToast('请输入邀请码')
    return
  }
  if (formData.value.loginPassword.trim() !== pwd.value.trim()) {
    showToast('两次密码不一致')
    return
  }
  loading.value = true
  signUp(formData.value)
    .then((res) => {
      if (res.code == '200') {
        loading.value = false
        showToast('注册成功！')
        if (autoLogin.value) {
          setTimeout(() => {
            if (res.code == '200' && res.data.satoken) {
              showToast('登陆成功！')
              let token = res.data.satoken
              userStore.setIsSign(true)
              userStore.setToken(token)
              setTimeout(() => {
                router.replace('/')
                userStore.getUserInfo()
              }, 500)
            } else {
              showToast(res.msg)
            }
          }, 1000)
        } else {
          router.push('/login')
        }
      } else {
        loading.value = false
        showToast(res.msg)
      }
    })
    .catch((err) => {
      console.log(err)
    })
}
</script>

