</template>

<script setup>
import { debounce } from 'lodash'
import { useRouter } from 'vue-router'
import { showToast } from 'vant'
import { signUp, mobileCode } from '@/api/user'
import { useMainStore } from '@/store/index'
import { useUserStore } from '@/store/user/index'
import PhonePopup from '@/components/phonePopup/index.vue'
const mainStore = useMainStore()
const userStore = useUserStore()
const router = useRouter()
const phoneCode = ref('86')
const isPwd = ref(true)
const isPwd2 = ref(true)
const pwd = ref('')
// 倒计时
const time = ref(0)
const flag = ref(false)
const formData = ref({
  areaNum: '',
  phoneNum: '',
  phone: '',
  loginPassword: '',
  activeCode: '',
  code: '',
  signType: 2
})
const close = () => {
  formData.value.phoneNum = ''
}
// 倒计时结束
const finish = () => {
  flag.value = false
}
const showBottom = ref(false)
const handleClose = (value) => {
  if (value.phoneCode) {
    phoneCode.value = value.phoneCode
  }
  showBottom.value = false
}
const loading = ref(false)
const getCode = debounce(() => {
  if (!formData.value.phoneNum) {
    showToast('请输入手机号')
    return
  }
  loading.value = true
  mobileCode('REGISTER', phoneCode.value + formData.value.phoneNum).then((res) => {
    if (res.code == '200') {
      loading.value = false
      showToast(res.msg)
      flag.value = true
      time.value = 60 * 1000
    } else {
      loading.value = false
    }
  })
}, 100)
const autoLogin = computed(() => {
  return mainStore.getLoginMethodList.autoLogin
})
const requireInviteCode = computed(() => {
  return mainStore.getLoginMethodList.inviteCodeRequired ? true : false
})
const showVerifyCode = computed(() => {
  return mainStore.getLoginMethodList.phoneCode
})
const handleSubmit = () => {
  if (!formData.value.phoneNum) {
    showToast('请输入手机号')
    return
  }

  if (!formData.value.loginPassword) {
    showToast('请输入登录密码')
    return
  }
  //登录密码必须包含 数字、字母、特殊符号
  if (
    !/^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,}$/.test(
      formData.value.loginPassword
    )
  ) {
    showToast('请填写6-16位包含数字字母特殊符号的密码')
    return
  }
  if (!pwd.value) {
    showToast('请再次输入登录密码')
    return
  }
  if (requireInviteCode.value && !formData.value.activeCode) {
    showToast('请输入邀请码')
    return
  }
  if (showVerifyCode.value && !formData.value.code) {
    showToast('请输入验证码')
    return
  }
  if (formData.value.loginPassword !== pwd.value) {
    showToast('两次密码不一致')
    return
  }
  formData.value.phone = phoneCode.value + formData.value.phoneNum
  signUp(formData.value)
    .then((res) => {
      if (res.code == '200') {
        showToast('注册成功！')
        if (autoLogin.value) {
          setTimeout(() => {
            if (res.code == '200' && res.data.satoken) {
              showToast('登录成功！')
              let token = res.data.satoken
              userStore.setIsSign(true)
              userStore.setToken(token)
              setTimeout(() => {
                router.replace('/')
                userStore.getUserInfo()
              }, 500)
            } else {
              showToast(res.msg)
            }
          }, 1000)
        } else {
          router.push('/login')
        }
      } else {
        showToast(res.msg)
      }
    })
    .catch((err) => {
      console.log(err)
    })
}
</script>

